import React, { useState,useEffect } from "react";
import Button from "./components/Button";
import CandidateCard from "./components/CandidateCard";
import "./styles.css";

export default function App() {
  const [data, setData] = useState([]);
  const [page,setPage]=useState(1);
  const [sort,setSort]=useState("asc")
 useEffect(()=>{
   fetchData(page)
       },[page,sort])
 const fetchData=(page,sort)=>{
             fetch(`http://localhost:8080/candidates?_sort=salary&_order=${sort}&_page=${page}&_limit=5`)
             .then((res)=>res.json())
             .then((res)=>setData(res))
 }
 const prevPage=()=>{
   setPage(page-1)
 }
 const nextPage=()=>{

   setPage(page+1)
}
const sortData=(title)=>{
  if(sort==true){
    setSort("asc")
  }else{
    setSort("")
  }
// if(title==="Sort by Ascending Salary"){
//   setSort("asc")
// }else{
//   setSort("desc")
// }
}
console.log(data)
  return (
    <div className="App">
      <div>
        <div id="loading-container">...Loading</div>

        <Button id="SORT_BUTTON" title={`Sort by Ascending Salary`} onClick={()=>{sortData(title)}} />
        <Button title="PREV" id="PREV" disabled={page===1} onClick={prevPage}/>
        <Button id="NEXT" title="NEXT" onClick={nextPage} />
      </div>
      {data.map((item) => <div>{<CandidateCard key={item.id} {...item} /> }</div>)}
    </div>
  );
}
